import React, { Component } from "react";
import Refund from "../Refund";
import Loading from "../QrLoading";
import QrCodeReader from "react-qr-reader";
import { invoiceLoad } from "../../actions/refundinvoice"; //importing action for RefundInvoice details
import { connect } from "react-redux";

//This component is used to display camera to scan the QR -code.
class ContainerQrCodeScanner extends Component {
  state = {
    loading: true,
    InvoiceScanned: ""
  };
  componentDidMount = () => {
    setTimeout(() => {
      this.setState({
        loading: false
      });
    }, 1000);
  };
  handleScan = data => {
    if (data) {
      this.props.invoiceLoad(data);
      this.setState({
        InvoiceScanned: data
      });
    }
  };
  handleError = err => {
    console.error(err);
  };
  render() {
    return this.state.loading ? (
      <Loading />
    ) : this.state.InvoiceScanned ? (
      <Refund />
    ) : (
      <React.Fragment>
        <div className="center">
          <h2> Scan your invoice</h2>
        </div>
        <div className="arrow">&#8674;</div>
        <div className="qrScanner">
          <QrCodeReader onError={this.handleError} onScan={this.handleScan} />
        </div>
      </React.Fragment>
    );
  }
}
export default connect(null, { invoiceLoad })(ContainerQrCodeScanner);
