import React from "react";
import { connect } from "react-redux";
import { invoiceLoad } from "../../actions/refundinvoice";
import { Link } from "react-router-dom";
import { Table } from "react-bootstrap";

function OverViewOfInvoiceRefund(props) {
  if (!props.invoice) return "No Invoice Available";
  return (
    <React.Fragment>
      <h2 className="center">Overview of Refunded Invoice Items:</h2>
      <Table role="table">
        <div role="rowgroup">
          <tr role="row">
            <th className="center2" role="columnheader">
              Sender
            </th>
            <th className="center2" role="columnheader">
              Invoice
            </th>
            <th className="center2" role="columnheader">
              Description
            </th>
            <th className="center2" role="columnheader">
              Amount
            </th>
            <th className="center2" role="columnheader">
              Refund %
            </th>
            <th className="center2" role="columnheader">
              Refund Amount
            </th>
          </tr>
        </div>
        <div role="rowgroup">
          {props.invoice &&
            props.invoice.map(invoicedetail => {
              return (
                <tr role="row" className="invoice1">
                  <td role="cell">{invoicedetail.sender}</td>
                  <td role="cell">{invoicedetail.invoice}</td>
                  <td role="cell">{invoicedetail.description}</td>
                  <td role="cell">{invoicedetail.amount}</td>
                  <td role="cell">{invoicedetail.percentageRefund}</td>
                  <td role="cell">{invoicedetail.refundAmount}</td>
                </tr>
              );
            })}
        </div>
      </Table>
      {/* </div> */}

      <Link to={`/qrscan`} className="link">
        Scan QR-Code
      </Link>
    </React.Fragment>
  );
}

// mapStateToProps is used for selecting part of data from the store.
// gettig invoice and PercentageRefund from the store
const mapStateToProps = state => {
  return {
    invoice: state.invoice
  };
};
export default connect(mapStateToProps, {
  invoiceLoad
})(OverViewOfInvoiceRefund);

