import React from "react";
import { connect } from "react-redux";
import { invoiceLoad } from "../../actions/refundinvoice";
import { Link } from "react-router-dom";

function InvoiceRefund(props) {
  if (!props.invoice) return "No Invoice Available";
  return (
    <React.Fragment>
      <h2>Overview of Refunded Invoice Items:</h2>
      <div role="table">
        <div role="rowgroup">
          <span role="row">
            <span role="columnheader">Sender</span>
            <span role="columnheader">Invoice_No</span>
            <span role="columnheader">Description</span>
            <span role="columnheader">Amount</span>
            <span role="columnheader">Refund %</span>
            <span role="columnheader">Refund Amount</span>
          </span>
        </div>
        <div role="rowgroup">
          {props.invoice &&
            props.invoice.map(invoices => {
              return (
                <div role="row" className="invoice1">
                  <span role="cell">{invoices.sender}</span>
                  <span role="cell">{invoices.invoice_no}</span>
                  <span role="cell">{invoices.description}</span>
                  <span role="cell">{invoices.amount}</span>
                  <span role="cell">{invoices.percentageRefund}</span>
                  <span role="cell">{invoices.refundAmount}</span>
                </div>
              );
            })}
        </div>
      </div>

      <Link to={`/qrscan`} className="link">
        Scan QR-Code
      </Link>
    </React.Fragment>
  );
}
const mapStateToProps = state => {
  return {
    invoice: state.invoice
  };
};
export default connect(mapStateToProps, {
  invoiceLoad
})(InvoiceRefund);
