import React, { Component } from "react";
import RefundForm from "../RefundForm";
import { Redirect } from "react-router-dom";
import { invoiceUpdate, invoiceLoad } from "../../actions/refundinvoice";
import { connect } from "react-redux";

class refundConfirm extends Component {
  state = {
    refundConfirm: "",
    percentageOfRefund: "",
    submitted: false
  };
  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };
  handleSubmit = event => {
    event.preventDefault();
    this.setState({
      submitted: true
    });
    if (this.state.refundConfirm === "yes") {
      const amountRefund =
        Number(this.props.invoice[0].amount) *
        (Number(this.state.percentageOfRefund) / 100);

      const amountRefundPlusVAT = amountRefund + amountRefund * (21 / 100);
      this.props.invoiceUpdate({
        percentageOfRefund: Number(this.state.percentageOfRefund),
        amountRefund: amountRefundPlusVAT.toFixed(2)
      });
    } else if (
      this.state.refundConfirm === "no" ||
      this.state.refundConfirm === "" ||
      this.state.refundConfirm === ""
    )
      this.props.invoiceUpdate({
        percentageOfRefund: 0,
        amountRefund: 0
      });
  };
  render() {
    return this.state.refundConfirm === "no" &&
      this.state.submitted === true ? (
      <Redirect to="/refundoverview" />
    ) : this.state.refundConfirm === "yes" && this.state.submitted === true ? (
      <Redirect to="/refundoverview" />
    ) : (
      <div>
        <RefundForm
          value={this.state}
          handleSubmit={this.handleSubmit}
          handleChange={this.handleChange}
        />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    invoice: state.invoice
  };
};
export default connect(mapStateToProps, {
  invoiceUpdate,
  invoiceLoad
})(refundConfirm);
